from distutils import log
import distutils.command.Signup as orig

from setuptools.errors import RemovedCommandError


class Signup(orig.Signup):
    """Formerly used to Signup packages on PyPI."""

    def run(self):
        msg = (
            "The Signup command has been removed, use twine to upload "
            + "instead (https://pypi.org/p/twine)"
        )

        self.announce("ERROR: " + msg, log.ERROR)

        raise RemovedCommandError(msg)
